brary.ResultSet).%New("%SYS.Monitor.SensorReading:SensorReadings") i Rset="" d $System.OBJ.DisplayError(%objlasterror) q d Rset.Execute(BeginDate,EndDate) #;SampleTime, Sensor, Item, Value While Rset.Next() { s Index=Index+1 s sensor = Rset.Get("Sensor"), item = Rset.Get("Item") i item'="" s sensor = sensor_": "_item s value = Rset.Get("Reading") s time = Rset.Get("SampleTime") s Record=$lb(time,sensor,value) i senopts'="*" { s vlist = vlist_$lb(value) } s Index(Index)=Record i Index=NumRecords q } k Rset s NumRecords=Index i NumRecords=0 w !,"No matching records found" goto SYSSENSORDATA s time = 0 f i=1:1:NumRecords { i time'=$li(Index(i),1) { w !,$li(Index(i),1) s time = $li(Index(i),1) } w ! w ?5,$li(Index(i),2) w ?60,$li(Index(i),3) } } else { #; Sensor "Xyzzy" Set Rset = ##class(%Library.ResultSet).%New("%SYS.Monitor.SensorReading:SensorReadingsBySensor") i Rset="" d $System.OBJ.DisplayError(%objlasterror) q d Rset.Execute(BeginDate,EndDate,senopts) #;SampleTime, Sensor, Item, Value: 2013-02-26 14:21:21 While Rset.Next() { s Index=Index+1 s item = Rset.Get("Item") s value = Rset.Get("Reading") s time = Rset.Get("SampleTime") s Record=$lb(time,item,value) s Index(Index)=Record i Index=NumRecords q } k Rset s NumRecords=Index i NumRecords=0 w !,"No matching records found" goto SYSSENSORDATA s time = 0 w !,"Records for sensor ",senopts f i=1:1:NumRecords { i time'=$li(Index(i),1) { w !,$li(Index(i),1) s time = $li(Index(i),1) } i $li(Index(i),2)="" { w ?25,$li(Index(i),3) } else { w ! w ?5,$li(Index(i),2) w ?60,$li(Index(i),3) } } } w ! q
SYSMGRE s $zt="" i $ze'["<INTERRUPT>" w !,"ERROR: "_$ZE Lock q MONHENTER d HEALTHMON^MONHEALTHMGR g SYSMONSTART MONAENTER d MONITORSETUP^%MONAPPMGR g SYSMONSTART
OPTION(prompt,default,max) [] PRIVATE { s $zt="OPTIONE" OPTION1 w !,prompt r Option i Option="" q default i '(Option?1.2N)!(Option<1)!(Option>max) w !!,"Please enter a number between 1 and "_max,! g OPTION1 q +Option OPTIONE s $zt="" i $ze'["<INTERRUPT>" w !,"ERROR: "_$ZE q 0 } NUMOPTION(prompt,default,min,max) [] PRIVATE { s $zt="NUMOPTIONE" NUMOPTION1 w !,prompt_"? "_default_" => " r Option i Option="" q default i '(Option?1.5N)!(Option<min)!(Option>max) w !!,"Please enter a number between "_min_" and "_max,! g NUMOPTION1 q +Option NUMOPTIONE s $zt="" i $ze'["<INTERRUPT>" w !,"ERROR: "_$ZE q -1 } STRINGOPTION(prompt,default,allownull) [] PRIVATE { s $zt="STRINGOPTIONE" STRINGOPTION1 w !,prompt_"? " i default'="" w default_" => " r Option i Option="",(default'="") q default i Option="",(allownull=0) w !,"Please enter a non-null value" g STRINGOPTION1 q Option STRINGOPTIONE s $zt="" i $ze'["<INTERRUPT>" w !,"ERROR: "_$ZE q -1 } GETNAME(Prompt,Mode,NameType,Default,RequireInput,Filter) PRIVATE { s namelist(0)=0 s OutName=$case(NameType,1:"Component class",2:"Sensor Object") s ext=$case(NameType,1:"ComponentClass:ComponentClasses",2:"SensorObject:Sensors") s Query="%SYS.Monitor."_ext Set Rset = ##class(%Library.ResultSet).%New(Query) i '$IsObject(Rset) d $SYSTEM.Status.DisplayError(%objlasterror) q -1 i $d(Filter) { d Rset.Execute(Filter) } else { d Rset.Execute() } While Rset.Next() { i (NameType=1) { s namelist($i(namelist(0)))=Rset.GetData(1)_"^"_Rset.GetData(3) } elseif NameType=2 { s namelist($i(namelist(0)))=Rset.GetData(1) } } GETNAME1 s $zt="GETNAMEE" w !,Prompt_"? " i $g(Default)'="" w Default_" => " R Name i Name="",$g(Default)'="" s Name=Default i (Name=""),+$g(RequireInput) w !,"Must enter a valid "_OutName g GETNAME1 i Name="" q Name i (Name="^")!($zcvt(Name,"U")="STOP") q -1 i Name="?" { w ! i NameType=1 { w !," Num",?6,Rset.GetColumnName(1),?60,Rset.GetColumnName(3) } i NameType=2 { w !," Num",?6,Rset.GetColumnName(1) } f i=1:1:namelist(0) { i NameType=1 { w !,$j(i,3)_") ",?6,$p(namelist(i),"^",1),?60,$p(namelist(i),"^",2) } i NameType=2 { w !,$j(i,3)_") ",?6,namelist(i) } } w ! g GETNAME1 } i Name=+Name { i (Name<1)!(Name>namelist(0)) w !,"Please enter a "_OutName_" or number between 1 and "_namelist(0) g GETNAME1 s Name=$p(namelist(Name),"^",1) w " ",Name } i (Mode=0),($$FINDNAME(Name)) w !,OutName_" "_Name_" already exists, please re-enter" g GETNAME1 i (Mode),('$$FINDNAME(Name)) w !,OutName_" "_Name_" does not exist, please re-enter" g GETNAME1 q Name GETNAMEE S $ZT="GETNAMEE1" i $ze["<INTERRUPT>" q -1 w !,"Error: "_$ze q -1 GETNAMEE1 s $zt="" w !,"Error: "_$ZE q -1 FINDNAME(name) s f=0 f i=1:1:namelist(0) i $p(namelist(i),"^",1)=name s f=1 q q f }
#include ISCYESNO

Method.Implementation:
#; mirror backup and async members get mirror sensors
s names =$p($G(^SYS("MIRRORSET")),"^",2) Q:names=""
f i=1:1:$length(names,",") s Mirrors($p(names,",",i)) = ""
#; get the mirror states
s mirname = ""
f {
	s mirname = $o(Mirrors(mirname)) q:mirname=""
	s Mirrors(mirname) = $li($SYSTEM.Mirror.GetMemberStatus(mirname))
}
#; if backup/async, get stats
s mir = $SYSTEM.Mirror.GetInfo()
i (mir["BACKUP") {
	#; the max size of a mirror journal file is 1GB. Thus for each journal file that we are behind,
	#; the sensor will reflect this as .5GB behind the primary for the first journal file, then 1GB for further files.
	s jrnlat = ##class(SYS.Mirror).DistanceFromPrimaryJournalFiles()
	i jrnlat<0 {
		d ..SetSensor("MirrorJournalLatencyFiles",jrnlat)
		d ..SetSensor("MirrorJournalLatencyBytes",0)
	} elseif jrnlat>1 {
		d ..SetSensor("MirrorJournalLatencyFiles",0)
		d ..SetSensor("MirrorJournalLatencyBytes",jrnlat)
	}

	s dblat = ##class(SYS.Mirror).DistanceFromPrimaryDatabases()
	i dblat<0 {
		d ..SetSensor("MirrorDatabaseLatencyFiles",-dblat)
		d ..SetSensor("MirrorDatabaseLatencyBytes",0)
	} elseif dblat>1 {
		d ..SetSensor("MirrorDatabaseLatencyFiles",0)
		d ..SetSensor("MirrorDatabaseLatencyBytes",dblat)
	}

	#; get latency times for every mirror (async has multiple mirrors)
	s mirname = ""
	f {
		s mirname = $o(Mirrors(mirname)) q:mirname=""
		s jrnlat = ##class(SYS.Mirror).JournalFilesLatency(mirname)
		d ..SetSensor("MirrorJournalLatencyTime",$s(+jrnlat'<0:jrnlat,1:0),mirname)
		s dblat = ##class(SYS.Mirror).DatabasesLatency(mirname)
		d ..SetSensor("MirrorDatabaseLatencyTime",$s(+dblat'<0:dblat,1:0),mirname)
	}
}
Q $$$OK

#; Shadow Status and Latency
s rspec = ##class(%ResultSet).%New("SYS.Shadowing.Shadow:Progress")
d rspec.Execute()
While rspec.Next() {
	s name=rspec.Data("Name")
	s stat = rspec.Data("Latency")
	d ..SetSensor("ShadowLatency",stat,name)
}

#; Shadow error information
if (##class(Security.Services).Exists($$$ServiceShadow,.ref)) && ref.Enabled {
	s rspec = ##class(%ResultSet).%New("SYS.Shadowing.DataSource:Connections")
	d rspec.Execute()
	While rspec.Next() {
		s shadowIP=rspec.Data("Shadow IP")
		s stat = rspec.Data("Latency")
		d ..SetSensor("ShadowConnectionsLatency",stat,shadowIP)
	}
}
Q $$$OK

s JobNumber=1, pcount=0
f  {
	s PID=$zu(61,17,JobNumber)
	i PID'=0 {
		s pcount=pcount+1 
	}
	s JobNumber=JobNumber+1 Q:JobNumber>$$$STmaxpid
}
d ..SetSensor("ProcessCount",pcount)
q $$$OK

#; get memory stats: total Physical, available Physical, total Page space, available Page space
s memstats = $zu(190,18)
i +memstats=-1 {
	Q $$$OK
}
s tPhys = +$p(memstats,",",1)
s aPhys = +$p(memstats,",",2)
s tPage = +$p(memstats,",",3)
s aPage = +$p(memstats,",",4)

#; check outputs for 0
i 'tPhys Q $$$OK
s Physpc = (tPhys-aPhys)*100\tPhys
i 'tPage {
	s Pagepc = 100
} else {
	s Pagepc = (tPage-aPage)*100\tPage
}
d ..SetSensor("PhysMemPerCentUsed",Physpc)
d ..SetSensor("PageSpacePerCentUsed",Pagepc)

Q $$$OK

try { #; CSP Gateway. Note that gateway sets external interrupts, so turn off at the end s GWRegistry = $system.CSP.GetGatewayRegistry() s GWList = GWRegistry.GetGatewayMgrs()
f i=1:1:GWList.Count() { s gateway = GWList.GetAt(i) i gateway.Port="" continue s GatewayName = gateway.IPAddress_":"_gateway.Port s gw(GatewayName) = "" k metrics
s starttime = $zh s sc = gateway.GetSystemMetrics(.metrics) s latency = ($zh - starttime) * 1000 i $d(metrics("Activity")) d ..SetSensor("CSPGatewayLatency",latency,GatewayName) d ..SetSensor("CSPActivity",$Get(metrics("Activity"))+0,GatewayName) d ..SetSensor("CSPActualConnections",$Get(metrics("ActualConnections"))+0,GatewayName) d ..SetSensor("CSPInUseConnections",$Get(metrics("InUseConnections"))+0,GatewayName) d ..SetSensor("CSPPrivateConnections",$Get(metrics("PrivateConnections"))+0,GatewayName) k gateway } k GWList, GWRegistry #; CSP sessions s i=0 f csp=0:1 s i=$o(^%cspSession(i)) q:i="" d ..SetSensor("CSPSessions",csp) } catch { ;ignore errors such as external interrupts } d $System.Util.SetInterruptEnable(0) Q $$$OK

s jnum = 0, maxtime = 0, TransOpenSecs = 0, TransOpenCount = 0
f {
	s txinfo = $zu(34,25,jnum) Q:'+txinfo
	s jnum = $p(txinfo,",",1)
	s pid = $p(txinfo,",",2)
	s jrnoff = $p(txinfo,",",3)
	s jrncnt = $p(txinfo,",",4)
	s pidlist(pid)=""
	i '$d(..OpenTx(pid)) {
		#; not seen before
		s time = ..ElapsedSec \ 2
		s ..OpenTx(pid) = time
		s ..OpenTx(pid,1) = jrncnt
		s ..OpenTx(pid,2) = jrnoff
	} else {
		#; if same tx, add seconds, else reinit
		i (..OpenTx(pid,1) = jrncnt) && (..OpenTx(pid,2) = jrnoff) {
			s time = ..OpenTx(pid) + ..ElapsedSec
			s ..OpenTx(pid) = time
		} else {
			#; started a new tx
			s time = ..ElapsedSec \ 2
			s ..OpenTx(pid) = time
			s ..OpenTx(pid,1) = jrncnt
			s ..OpenTx(pid,2) = jrnoff
		}
	}
	i time>maxtime s maxtime = time
	s TransOpenSecs = TransOpenSecs + time
	s TransOpenCount = TransOpenCount + 1
}
d ..SetSensor("TransOpenSecsMax",maxtime)
d ..SetSensor("TransOpenSecs",TransOpenSecs)
d ..SetSensor("TransOpenCount",TransOpenCount)

i TransOpenCount {
	#; go thru our last list and remove pids with no tx or that no longer exist
	s pid = ""
	f {
		s pid = $o(..OpenTx(pid)) q:pid=""
		i '$d(pidlist(pid)) k ..OpenTx(pid)
	}
} else {
	#; there are no open local tx
	k ..OpenTx
}
#; ECP Server Tx
s txinfo = $zu(34,24)
i txinfo="" {
	#; there are no ECP tx open
	k ..ECPOpenTx
	Q
}
s jnum = 0, maxtime = 0, ECPTransOpenSecs = 0, ECPTransOpenCount = 0
f {
	s txinfo = $zu(34,25,jnum) Q:'+txinfo
	s jnum = $p(txinfo,",",1)
	s pid = $p(txinfo,",",2)
	s jrnoff = $p(txinfo,",",3)
	s jrncnt = $p(txinfo,",",4)
	s pidlist(pid)=""
	i '$d(..ECPOpenTx(pid)) {
		#; not seen before
		s time = ..ElapsedSec \ 2
		s ..ECPOpenTx(pid) = time
		s ..ECPOpenTx(pid,1) = jrncnt
		s ..ECPOpenTx(pid,2) = jrnoff
	} else {
		#; if same tx, add seconds, else reinit
		i (..ECPOpenTx(pid,1) = jrncnt) && (..ECPOpenTx(pid,2) = jrnoff) {
			s time = ..ECPOpenTx(pid) + ..ElapsedSec
			s ..ECPOpenTx(pid) = time
		} else {
			#; started a new tx
			s time = ..ElapsedSec \ 2
			s ..ECPOpenTx(pid) = time
			s ..ECPOpenTx(pid,1) = jrncnt
			s ..ECPOpenTx(pid,2) = jrnoff
		}
	}
	i time>maxtime s maxtime = time
	s ECPTransOpenSecs = ECPTransOpenSecs + time
	s ECPTransOpenCount = ECPTransOpenCount + 1
}
d ..SetSensor("ECPTransOpenSecsMax",maxtime)
d ..SetSensor("ECPTransOpenSecs",ECPTransOpenSecs)
d ..SetSensor("ECPTransOpenCount",ECPTransOpenCount)
i ECPTransOpenCount {
	#; go thru our last list and remove pids with no tx or that no longer exist
	s pid = ""
	f {
		s pid = $o(..ECPOpenTx(pid)) q:pid=""
		i '$d(pidlist(pid)) k ..ECPOpenTx(pid)
	}
} else {
	#; there are no open local tx
	k ..ECPOpenTx
}
Q $$$OK

s sc=$$$OK
i '##class(Security.Applications).%ExistsId("/api/dashboard") {
	s app=##class(Security.Applications).%New()
	s app.Name="/api/dashboard",app.Description="System Monitor Dashboard REST API"
	s app.NameSpace="%SYS",app.DispatchClass="%Api.Dashboard",app.Timeout=3600
	#; create with same authentication as the MP Operations app
	i ##class(Security.Applications).Exists("/csp/sys/op",.op) {
		s app.AutheEnabled=op.AutheEnabled
	}
	s sc=app.%Save()
}
s ^SYS("Security","CSP","AllowClass","/api/dashboard/","%Api.Dashboard")=1
q sc
